mapper.select:
select 

from e_brand_daily_achievement



    and brand_id = #{brandId,jdbcType=INTEGER}


    and mall_id = #{mallId,jdbcType=INTEGER}


    and sales_date = #{salesDate,jdbcType=TIMESTAMP}


    and salesofamount = #{salesofamount,jdbcType=REAL}


    and sales_volume = #{salesVolume,jdbcType=REAL}


    and sales_times = #{salesTimes,jdbcType=INTEGER}


    and is_holiday = #{isHoliday,jdbcType=BIT}


    and holiday_id = #{holidayId,jdbcType=VARCHAR}


    and date_type = #{dateType,jdbcType=VARCHAR}


    and dayofweek = #{dayofweek,jdbcType=VARCHAR}


    and week_num = #{weekNum,jdbcType=INTEGER}


    and weather = #{weather,jdbcType=VARCHAR}


    and customer_volume = #{customerVolume,jdbcType=INTEGER}


    and car_volume = #{carVolume,jdbcType=INTEGER}


    and creation_date = #{creationDate,jdbcType=TIMESTAMP}


    and created_by = #{createdBy,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and update_by = #{updateBy,jdbcType=VARCHAR}


    and is_alive = #{isAlive,jdbcType=BIT}
