mapper.select:
select

from e_person


		and person_number = #{personNumber,jdbcType=VARCHAR}


		and person_name = #{personName,jdbcType=VARCHAR}


		and login_name = #{loginName,jdbcType=VARCHAR}


		and password = #{password,jdbcType=VARCHAR}


		and sex = #{sex,jdbcType=BIT}


		and age = #{age,jdbcType=INTEGER}


		and birthday = #{birthday,jdbcType=TIMESTAMP}


		and image_path = #{imagePath,jdbcType=VARCHAR}


		and entrant_date = #{entrantDate,jdbcType=TIMESTAMP}


		and mac_address = #{macAddress,jdbcType=VARCHAR}


		and home_address = #{homeAddress,jdbcType=VARCHAR}


		and remark = #{remark,jdbcType=VARCHAR}


		and creation_date = #{creationDate,jdbcType=TIMESTAMP}


		and tel_number = #{telNumber,jdbcType=VARCHAR}


		and cell_phone = #{cellPhone,jdbcType=VARCHAR}


		and created_by = #{createdBy,jdbcType=VARCHAR}


		and update_date = #{updateDate,jdbcType=TIMESTAMP}


		and updated_by = #{updatedBy,jdbcType=VARCHAR}


		and is_alive = #{isAlive,jdbcType=BIT}


		and email = #{email,jdbcType=VARCHAR}


		and job_title = #{jobTitle,jdbcType=VARCHAR}


		and authority = #{authority,jdbcType=VARCHAR}


		and mall_id = #{mallId,jdbcType=INTEGER}
