mapper.select:

select 

from e_trade_monthly_achievement


    and trade_id = #{tradeId,jdbcType=INTEGER}


    and mall_id = #{mallId,jdbcType=INTEGER}


    and DATE_FORMAT(sale_month,'%Y-%m') = DATE_FORMAT(#{saleMonth,jdbcType=DATE},'%Y-%m')


    and sales_amount = #{salesAmount,jdbcType=REAL}


    and sales_volume = #{salesVolume,jdbcType=INTEGER}


    and sales_times = #{salesTimes,jdbcType=INTEGER}


    and m_rent = #{mRent,jdbcType=REAL}


    and m_property = #{mProperty,jdbcType=REAL}


    and m_other_charge1 = #{mOtherCharge1,jdbcType=REAL}


    and m_other_charge2 = #{mOtherCharge2,jdbcType=REAL}


    and m_other_charge3 = #{mOtherCharge3,jdbcType=REAL}


    and m_fixed_charge1 = #{mFixedCharge1,jdbcType=REAL}


    and m_fixed_charge2 = #{mFixedCharge2,jdbcType=REAL}


    and m_fixed_charge3 = #{mFixedCharge3,jdbcType=REAL}


    and update_dates = #{updateDates,jdbcType=TIMESTAMP}


    and customer_volume = #{customerVolume,jdbcType=INTEGER}


    and car_volume = #{carVolume,jdbcType=INTEGER}


    and creation_date = #{creationDate,jdbcType=TIMESTAMP}


    and created_by = #{createdBy,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and updated_by = #{updatedBy,jdbcType=VARCHAR}


    and is_alive = #{isAlive,jdbcType=BIT}
